// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/utils/math/SafeMath.sol";


contract Lottery2 {
    using SafeMath for uint256;
    address payable public immutable receiver;
    address payable public manager;
    uint256 public price;
    address payable public winner;
    address[] public participants;
    mapping(address => uint256) public funds;
    uint256 totaltickets;
    uint256 public winnerIndex;
    uint256 public winningAmount;
    uint256 public leftAmount;
    uint256 public NoOfParticipants;

    receive() external payable {}

    constructor() {
        receiver = payable(address(this));
    }

    /////////////////////////////////////////Manger-Section////////////////////////////////////////////////////////
    modifier onlyManager() {
        require(
            msg.sender == manager,
            "Only the manager can call this function."
        );
        _;
    }

    function getContractBalance() public view returns (uint256) {
        return address(this).balance;
    }

    function setManager(address account) external {
        manager = payable(account);
    }

    function setTickets(uint256 number) external onlyManager {
        totaltickets = number;
    }

    function setPrice(uint256 _price) external onlyManager {
        price = _price;
    }

    function getParticipants() external view returns (address[] memory) {
        return participants;
    }

    function getFunds() external view returns (uint256[] memory) {
        uint256[] memory Funds = new uint256[](NoOfParticipants);
        for (uint256 i = 0; i < NoOfParticipants; i++) {
            address buyer = participants[i];
            Funds[i] = funds[buyer];
        }
        return Funds;
    }

    /////////////////////////////////////////Participation-Section////////////////////////////////////////////////////////
    function participate(uint256 numTickets) external payable {
        address payable buyer = payable(msg.sender);
        require(buyer != manager, "The manager cannot buy!");
        require(
            numTickets <= (totaltickets / 2),
            "You cannot buy more than half of the total tickets!"
        );
        require(!hasParticipated(msg.sender), "You have already participated!");
        uint256 cost = numTickets.mul(price);
        require(msg.value >= cost, "Not enough balance in your account!");

        uint256 extraAmount = msg.value.sub(cost);
        (buyer).transfer(extraAmount);

        participants.push(msg.sender);
        funds[msg.sender] = msg.value.sub(extraAmount);
        NoOfParticipants++;
    }

    function hasParticipated(address account) public view returns (bool) {
        for (uint256 i = 0; i < participants.length; i++) {
            if (account == participants[i]) {
                return true;
            }
        }
        return false;
    }

    /////////////////////////////////////////Winner-Section////////////////////////////////////////////////////////
    function withdrawFunds() public {
        for (uint256 i = 0; i < participants.length; i++) {
            address payable buyer = payable(participants[i]);
            uint256 withdrawAmount = funds[buyer];
            if (withdrawAmount > 0) {
                buyer.transfer(withdrawAmount);
                funds[buyer] = 0;
            }
        }
        NoOfParticipants = 0;
        delete participants;
    }

    function getRandom() internal view returns (uint256) {
        return
            uint256(
                keccak256(
                    abi.encodePacked(
                        block.timestamp,
                        NoOfParticipants,
                        address(this)
                    )
                )
            );
    }

    function payWinner() public {
        require(
            participants.length >= 3,
            "There should be at least 3 participants."
        );
        uint256 range = participants.length;
        uint256 random = getRandom();
        uint256 randomIndex = uint256(
            keccak256(abi.encodePacked(random, block.number, msg.sender))
        ) % range;
        winnerIndex = randomIndex;
        winner = payable(participants[winnerIndex]);
        winningAmount = getContractBalance() / 2;
        leftAmount = getContractBalance() - winningAmount;
        winner.transfer(winningAmount);
        manager.transfer(leftAmount);
        participants = new address payable[](0);
        for (uint256 i = 0; i < participants.length; i++) {
            address payable buyer = payable(participants[i]);
            uint256 withdrawAmount = funds[buyer];
            if (withdrawAmount > 0) {
                funds[buyer] = 0;
            }
        }
        NoOfParticipants = 0;
    }

    function getWinner() external view onlyManager returns (address) {
        return winner;
    }

    /////////////////////////////////////////Decision-Section////////////////////////////////////////////////////////
    function getfundsneeded() public view returns (uint256) {
        return (totaltickets.mul(price));
    }

    function decide() external onlyManager {
       
